mapper.select:
WITH RankedPhotos AS ( SELECT D.strf_id, D.pic_name, ROW_NUMBER() OVER (PARTITION BY D.strf_id ORDER BY D.pic_name) AS rn FROM strf_pic D ) SELECT A.booking_id, C.strf_id, C.title, RP.pic_name, A.check_in, A.check_out, A.total_payment, A.state FROM booking A LEFT JOIN menu B ON A.menu_id = B.menu_id LEFT JOIN stay_tour_restaur_fest C ON B.strf_id = C.strf_id LEFT JOIN RankedPhotos RP ON C.strf_id = RP.strf_id AND RP.rn = 1 LEFT JOIN business_num E ON C.busi_num = E.busi_num LEFT JOIN user F ON E.user_id = F.user_id WHERE F.user_id = #{userId} AND C.category = 'STAY'
AND (
A.check_in >= #{startDate}
OR
A.check_out <= #{endDate}
)
